import React, { useState, useEffect } from "react";
import { Grid, TextField, makeStyles } from "@material-ui/core";
import VehiclesModal, { vehiclesModal } from "./VehiclesModal";
import LevelModal from "./LevelModal";
import { useStyles } from "./OfferStyles";
import Button from "./Button";




 class AddNewOfferForm extends React.Component {
  state= {
    id: 0,
    title: "",
    onePerson: "",
    twoPeople: "",
    Group: "",
    vehicles: "",
    city: "",
    river: "",
    postalCode: "",
    street: "",
    streetNumber: "",
    description: "",
    level: "",
    imageURL: "",
  };

  handleOnChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }
render(){
  const classes = useStyles();

  return (
    <form className={classes.root}>
          <div class="diwek">
            <TextField
              variant="outlined"
              label="Jak się nazywa Twoja firma?"
              onChange={this.handleOnChange}
              name="title"
            />
          </div>
          <div style={{display: "flex", alignSelf:"center", lineHeight:1.10, width: "100%", marginTop: '10px'}}>
            <LevelModal />
            <div style={{display: "flex", width: "81%"}}>
            <TextField
              variant="outlined"
              label="Cena za osobę"
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="onePerson"
            />
            <TextField
              variant="outlined"
              label="Cena za dwie osoby"
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="twoPeople"
            />
            <TextField
              variant="outlined"
              label="Cena za osobę dla grup"
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="group"
            />
            <VehiclesModal />
          </div></div>

          <div style={{
            display: "flex", 
            justifyContent: "center",
            marginTop: "10px"}}>
            <div style={{display: "flex"}}>
            <TextField
              variant="outlined"
              label="W jakim mieście?"
              onChange={this.handleOnChange}
              name="city"
            />
            <TextField
              variant="outlined"
              label="Na jakiej wodzie?"
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="river"
            />
            <TextField
              variant="outlined"
              label="Podaj kod pocztowy."
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="postalCode"
            />
            <TextField
              variant="outlined"
              label="Na jakiej ulicy?"
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="street"
            />
            <TextField
              variant="outlined"
              label="Jaki jest numer lokalu?"
              onChange={this.handleOnChange}
              style={{marginLeft : "10px"}}
              name="streetNumber"
            />
            </div>
          </div>
          <div style={{display: "flex", width:"100%"}}>
          <TextField
            variant="outlined"
            label="Opisz swoje miejsce"
            onChange={this.handleOnChange}
            style={{marginTop : "10px", width: "100%"}}
            name="description"
          />
          </div>
        <div
          style={{ display: "flex", justifyContent: "center", width: "100%", marginTop:"10px" }}
        >

        </div>
    </form>
  );
}
}
export default AddNewOfferForm;
