import React, { useState, useEffect } from "react";


export const useStyles = makeStyles((theme) => ({
    root: {
  '& .MuiInputBase-root' : {
    width: '80%',
    margin: theme.spacing(1),
    margin: 0,
    padding: 0
  },
  '& .MuiGrid-item' : {
    display: 'flex',
    flexFlow: 'row',
    flexWrap: 'wrap',
    margin: 0,
    padding: 0
  },
  '& .MuiGrid-grid-xs-6' : {
    flexBasis: '100%',
    maxWidth: '100%'
  }
    }
  })
  )
