import React, { Component } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
const DATABASE_URL = "https://dancing-app-77d2a.firebaseio.com";
export default class BarChart extends Component {
  state = {
    offers: [],
    chartData: {
      beginAtZero: true ,
      labels: ['Kajaki jednoosobowe', 'Kajaki Wieloosobowe', 'Rowery wodne'],
      datasets: [
        {
          label: "Population",
          data: [],
          backgroundColor: 
          ['#89C7CC',
          '#04738D',
          '#89C7CC',
          ]
        }
      ]
  }
  };
  labels = ['łatwy', 'średni', 'trudny'];
  componentDidMount() {
    fetch(`${DATABASE_URL}/offers.json`)
      .then((response) => response.json())
      .then((data) => {
        const formattedData = Object.keys(data).map((key) => ({
          id: key,
          ...data[key],
        }));
        this.setState({
          offers: formattedData,
        });
      }).then(this.filteringData);
  }
  filteringData = () => {
     let difficultyLevels = this.labels.map((difficulty) => {
      return this.state.offers.filter((element) => {
        return element.level === difficulty;
      }).length;
    });
    this.setState({
      chartData: {

        labels: ['Kajaki jednoosobowe', 'Kajaki Wieloosobowe', 'Rowery wodne'],
        datasets: [
          {
            label: "Population",
            data: difficultyLevels,
            backgroundColor: 
            ['#89C7CC',
            '#04738D',
            '#89C7CC',
            ]
          }
        ],
        options: {
          ticks: {
            beginAtZero:true,
            mirror:false,
            suggestedMin: 0,
            suggestedMax: 500,
          },
      }

    }
    });
  }
  render() {
    return (
      <div
        className="chart"
        style={{
          width: "600px",
          height: "1000px",
          marginLeft: "100px",
        }}
      >
        <Bar
          data={this.state.chartData}
          options={{
            title: {
              display: true,
              text: "Ilość obiektów w naszej bazie",
              fontSize: 25,
            },
            legend: {
              display: false,
              position: "right",
            },
          }}
        />
      </div>
    );
  }
}
