import React, { useState, useEffect } from "react";
import { Grid, TextField, makeStyles } from "@material-ui/core";
import VehiclesModal, { vehiclesModal } from "./VehiclesModal";
import LevelModal from "./LevelModal";
import { useStyles } from "./OfferStyles";
import {default as UUID} from "node-uuid";import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';

import Button from "./Button";
const DATABASE_URL = "https://dancing-app-77d2a.firebaseio.com";

export default class AddNewOfferForm extends React.Component {
  state = {
      title: "title",
      id: UUID.v4(),
      offer:[
        {
            type: "kajak jednoosobowy",
            numberOfPeople: 1,
            price: 15
        },
        {
            type: "kajak dwusobowy",
            numberOfPeople: 2,
            price: 25
        },
        {
            type: "kajak group",
            numberOfPeople: 10,
            price: 66
        },
        {
            type: "rowerek 4 osobowy",
            numberOfPeople: 4,
            price: 12
        }
      ],
    level: "",
    location: {
        city: "",
        river: "",
        postalCode: "",
        street: "",
        streetNumber: "",
        coordinates: [54.343922, 18.659500]

    },
    imageUrl: "",
    description: ""
  };

  handleOnChange = (event) => {

    this.setState({
        [event.target.name]: event.target.value
    })
}
handleOnLocationChange = (event) => {
  this.setState({
      [event.target.name]: event.target.value
  })
}
handleOnSubmit = (event) => {
  event.preventDefault();

  fetch(`${DATABASE_URL}/offers.json`, {
      method: 'POST',
      body: JSON.stringify(this.state)
  }).then(() => {
      this.props.setOpenPopup();
  })
}

  render() {
    const { setOpenPopup } = this.props;
    return (
      <form className={this.classes}
      onSubmit={this.handleOnSubmit}
      >
        <div class="diwek">
          <TextField
            variant="outlined"
            label="Jak się nazywa Twoja firma?"
            value={this.state.title}
            name="title"
            onChange={this.handleOnChange}
          />
        </div>
        <div
          style={{
            display: "flex",
            alignSelf: "center",
            lineHeight: 1.1,
            width: "100%",
            marginTop: "10px",
          }}
        >
         <FormControl component="fieldset">
      <FormLabel component="legend">Poziom trudności</FormLabel>
      <RadioGroup aria-label="level" name="level" value={this.state.level} onChange={this.handleOnChange}>
        <FormControlLabel value="łatwy" control={<Radio />} label="Łatwy" />
        <FormControlLabel value="średni" control={<Radio />} label="Średni" />
        <FormControlLabel value="trudny" control={<Radio />} label="Trudny" />      </RadioGroup>
    </FormControl>
          <div style={{ display: "flex", width: "81%" }}>
            <TextField
              variant="outlined"
              label="Cena za osobę"
              name="onePerson"
              value={this.state.onePerson}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Cena za dwie osoby"
              name="twoPeople"
              value={this.state.twoPeople}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Cena za osobę dla grup"
              name="Group"
              value={this.state.Group}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <VehiclesModal />
          </div>
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "10px",

          }}
        >
          <div style={{ display: "flex" }}>
            <TextField
              variant="outlined"
              label="W jakim mieście?"
              name="location.city"
              value={this.state.location.city}
              onChange={this.handleOnLocationChange}
            />
            <TextField
              variant="outlined"
              label="Na jakiej wodzie?"
              value={this.state.location.river}
              name="river"
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Podaj kod pocztowy."
              value={this.state.location.postalCode}
              style={{ marginLeft: "10px" }}
              name="postalCode"
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Na jakiej ulicy?"
              value={this.state.location.street}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
              name="street"
            />
            <TextField
              variant="outlined"
              label="Jaki jest numer lokalu?"
              value={this.state.location.streetNumber}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
              name="streetNumber"
            />
          </div>
        </div>
        <div style={{ display: "flex", width: "100%" }}>
          <TextField
            variant="outlined"
            label="Opisz swoje miejsce"
            value={this.state.description}
            style={{ marginTop: "10px", width: "100%" }}
            onChange={this.handleOnChange}
            name="description"
          />
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <Button
            component="div"
            color="primary"
            type="submit"
            text="Dodaj"
            // className={this.classes.ButtonAdd}
            style={{
              width: "100px",
              margin: "3px",
            }}
            onClick={this.handleOnSubmit}
          />
          <Button
            component="div"
            type="reset"
            color="primary"
            text="Resetuj"
            // className={this.classes.ButtonReset}
            style={{
              width: "100px",
              margin: "3px",
            }}
            onClick={() => {
              setOpenPopup(false);
            }}
          />
        </div>
      </form>
    );
  }
}
