import React, {Component} from 'react';
import {Bar, Line, Pie} from 'react-chartjs-2';
const DATABASE_URL = "https://dancing-app-77d2a.firebaseio.com";

export default class BarChart extends Component {
  state = {
    offers: [],
    chartData: [0,0,0]
  };
  labels = ["łatwy", "średni", "trudny"];
  backgroundColor = [
    "#89C7CC",
    "#04738D",
    "#89C7CC",
    "#04738D",
    "#560F0F",
];
  componentDidMount() {
    fetch(`${DATABASE_URL}/offers.json`)
      .then((response) => response.json())
      .then((data) => {
        const formattedData = Object.keys(data).map((key) => ({
          id: key,
          ...data[key],
        }));
        this.setState({
          offers: formattedData,
        });
        this.filteringData();
      });

  }
  filteringData = () => {
     let difficultyLevels = this.labels.map((difficulty) => {
      return this.state.offers.filter((element) => {
        return element.level === difficulty;
      }).length;
    });


  }
  render() {
    if(this.state.offers !== undefined){
      console.log(this.chartData);
      console.log(this.difficultyLevels)

    }
    return (
      <div
        className="chart"
        style={{
          width: "600px",
          height: "400px",
          marginLeft: "100px",
        }}
      >
        <Bar
          data={this.state.difficultyLevels}
          options={{
            title: {
              display: true,
              text: "Ilość obiektów w naszej bazie",
              fontSize: 25,
            },
            legend: {
              display: false,
              position: "right",
            },
          }}
        />
      </div>
    );
  }
}
