import { makeStyles } from '@material-ui/core/styles';
import homeImage from "./images/homeImage.jpg";
import paddle from "./images/paddle.png";
export const homeStyles = makeStyles((theme) => ({
    section: {
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        '&::before': {
            content: '',
            position: "absolute",
            bottom: 0,
            width: '100%',
            height: "100px",
            background: "linear-gradient(to top, #0a2a43, transparent)",
            zIndex: 1000000
        }
    },
    homeImage: {
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        objectFit: "cover",
        pointerEvents: "none"
    },
    paddle: {
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        objectFit: "cover",
        pointerEvents: "none"
    }

}))
