import React from 'react';
import { offers } from './OffersList';
import OfferCard from './OfferCard';


class Cards extends React.Component {

    state =  {
        offers : offers,
    }


render() {
    return <div
    style={{
        display: "flex",
        // flexWrap: "wrap",
        margin: "5px",
        width: "24%"
    }}>

            {this.state.offers.map(offer => (
                <div key={offer.id}>
                    <OfferCard offer={offer} />
                </div>
            ))}

    </div>

}
}

export default Cards;
