import React, { useState, useEffect } from "react";
import { Grid, TextField, makeStyles } from "@material-ui/core";
import VehiclesModal, { vehiclesModal } from "./VehiclesModal";
import LevelModal from "./LevelModal";
import { useStyles } from "./OfferStyles";
import Button from "./Button";
const DATABASE_URL = "https://dancing-app-77d2a.firebaseio.com";

export default class AddNewOfferForm extends React.Component {
  state = {
      title: "",
      onePerson: "",
      twoPeople: "",
      Group: "",
      vehicles: "",
      city: "",
      river: "",
      postalCode: "",
      street: "",
      streetNumber: "",
      description: "",
      level: "",
      imageURL: "",
      id: 0
  };


  handleOnChange = (event) => {
    console.log(this.state)
    this.setState({
        [event.target.name]: event.target.value
    })
}
generateId = ()=>{
  let x = Math.floor(Math.random() * (1000 - 15 + 1)) + 15
  return x
}
handleOnSubmit = (event) => {
  this.setState({
    id: this.x
  })
  event.preventDefault();

  fetch(`${DATABASE_URL}/offers.json`, {
      method: 'POST',
      body: JSON.stringify(this.state)
  }).then(() => {
      this.props.setOpenPopup();
  })
}

  render() {
    const { setOpenPopup } = this.props;
    return (
      <form className={this.classes}
      onSubmit={this.handleOnSubmit}
      >
        <div class="diwek">
          <TextField
            variant="outlined"
            label="Jak się nazywa Twoja firma?"
            value={this.state.title}
            name="title"
            onChange={this.handleOnChange}
          />
        </div>
        <div
          style={{
            display: "flex",
            alignSelf: "center",
            lineHeight: 1.1,
            width: "100%",
            marginTop: "10px",
          }}
        >
          <LevelModal />
          <div style={{ display: "flex", width: "81%" }}>
            <TextField
              variant="outlined"
              label="Cena za osobę"
              name="onePerson"
              value={this.state.onePerson}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Cena za dwie osoby"
              name="twoPeople"
              value={this.state.twoPeople}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Cena za osobę dla grup"
              name="Group"
              value={this.state.Group}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <VehiclesModal />
          </div>
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "10px",

          }}
        >
          <div style={{ display: "flex" }}>
            <TextField
              variant="outlined"
              label="W jakim mieście?"
              name="city"
              value={this.state.city}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Na jakiej wodzie?"
              value={this.state.rier}
              name="river"
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Podaj kod pocztowy."
              value={this.state.postalCode}
              style={{ marginLeft: "10px" }}
              name="postalCode"
              onChange={this.handleOnChange}
            />
            <TextField
              variant="outlined"
              label="Na jakiej ulicy?"
              value={this.state.street}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
              name="street"
            />
            <TextField
              variant="outlined"
              label="Jaki jest numer lokalu?"
              value={this.state.streetNumber}
              style={{ marginLeft: "10px" }}
              onChange={this.handleOnChange}
              name="streetNumber"
            />
          </div>
        </div>
        <div style={{ display: "flex", width: "100%" }}>
          <TextField
            variant="outlined"
            label="Opisz swoje miejsce"
            value={this.state.description}
            style={{ marginTop: "10px", width: "100%" }}
            onChange={this.handleOnChange}
            name="description"
          />
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <Button
            component="div"
            color="primary"
            type="submit"
            text="Dodaj"
            // className={this.classes.ButtonAdd}
            style={{
              width: "100px",
              margin: "3px",
            }}
            onClick={this.handleOnSubmit}
          />
          <Button
            component="div"
            type="reset"
            color="primary"
            text="Resetuj"
            // className={this.classes.ButtonReset}
            style={{
              width: "100px",
              margin: "3px",
            }}
            onClick={() => {
              setOpenPopup(false);
            }}
          />
        </div>
      </form>
    );
  }
}
