import React, {Component} from 'react';
import {Bar, Line, Pie} from 'react-chartjs-2';

class BarChart extends Component {

  constructor(props) {
      super(props);
      this.state = {
          chartData : {
            labels: ['marzec', 'kwiecień', 'maj', 'czerwiec', 'lipiec'],
            datasets: [
              {

                data: [
                  15,
                  24,
                  46,
                  70,
                  130
                ],
                backgroundColor: 
                ['rgb(145, 143, 71)',
                'rgb(180, 180, 180)',
                'rgb(0, 0, 0)',
                'rgba(255, 99, 71, 0.5)',
                'rgb(106, 90, 205)'

                ]
              }
            ]
          }

      }
  }
    render () {

      return (
        <div className="chart"
        style={{
          width: "600px", 
          height: "400px",
          }}>
            <Bar 
            data={this.state.chartData}

            options={{
              title: {
                display: true,
                text:'Ilość obiektów w naszej bazie',
                fontSize: 25
              },
              legend: {
                display: true,
                position: 'right'
              }
            }}
            />
        </div>
      )

    }

}

export default BarChart;
