import { CardActions, CardContent } from "@material-ui/core";
import React from "react";
import Card from "@material-ui/core/Card";
import Button from "../AddNewOfferForm/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Slide from "@material-ui/core/Slide";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function OfferCard(props) {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div
      style={{
        display: "flex",
        width: "320px",
      }}
    >
      <Card
        className="OffersContainer"
        style={{
          width: "100%",
          height: "350px",
          margin: "8px",
        }}
      >
        <CardContent>
          <h2
            className="title"
            style={{
              marginTop: "-2px",
              textAlign: "center",
              marginBottom: "3px",
            }}
          >
            {props.offer.title}
          </h2>
          <p
            style={{
              textAlign: "center",
              margin: "2px",
            }}
          >
            Poziom trudności: {props.offer.level}
          </p>
          <div className="imgContainer">
            <img
              src={props.offer.imageUrl}
              className="offerImage"
              alt="offerImage"
              style={{
                width: "100%",
                height: "180px",
              }}
            />
          </div>
        </CardContent>
        <CardActions
          className="cardActions"
          style={{ display: "flex", justifyContent: "center" }}
        >
          <Button
            text="Zobacz więcej!"
            variant="contained"
            type="submit"
            onClick={handleClickOpen}
          />
          <Dialog
            open={open}
            TransitionComponent={Transition}
            keepMounted
            onClose={handleClose}
            aria-labelledby="alert-dialog-slide-title"
            aria-describedby="alert-dialog-slide-description"
          >
            <DialogTitle id="alert-dialog-slide-title">
                <h1 style={{
                    textAlign: "center",
                    marginTop: "3px",
                    marginBottom: "0px"
                }}>   {props.offer.title}</h1>
            </DialogTitle>
            <DialogContent>
            <img
              src={props.offer.imageUrl}
              className="offerImage"
              alt="offerImage"
              style={{
                width: "100%",
                height: "350px",
              }}
            />
            <DialogContentText>
                <b>Poziom trudności:</b> {props.offer.level}<br/>
                <b>Adres:</b> {props.offer.location.city} ul. {props.offer.location.street} {props.offer.location.streetNumber}, {props.offer.location.postalCode}<br/>
              <b>Opis miejsca:</b> {props.offer.description}
            </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button
              variant="contained"
              color="primary"
              text="Zobacz na mapie"
              style={{
                width: "200px",
                height: "50px"
            }}/>
              <Button 
              onClick={handleClose} 
              variant="contained" 
              color="primary"
              text="Wróć"
              style={{
                  width: "100px",
                  height: "50px"
              }}/>

            </DialogActions>
          </Dialog>
        </CardActions>
      </Card>
    </div>
  );
}
