import React, { useState, useEffect } from "react";
import { Grid, TextField, makeStyles } from '@material-ui/core';
import VehiclesModal, {vehiclesModal} from "./VehiclesModal";

const useStyle = makeStyles(theme => ({
  root: {
'& .MuiInputBase-root' : {
  width: '80%',
  margin: theme.spacing(1),
  margin: 0,
  padding: 0
},
'& .MuiGrid-item' : {
  display: 'flex',
  flexFlow: 'row',
  flexWrap: 'wrap',
  margin: 0,
  padding: 0
}
  }
})
)

const initialValues = {
  id: 0,
  title: "",
  onePerson: "",
  twoPeople: "",
  Group: "",
  vehicles: "",
  city: "",
  river: "",
  postalCode: "",
  street: "",
  streetNumber: "",
  description: "",
  level: "",
  imageURL: "",
};

export default function AddNewOfferForm() {
  const [values, setValues] = useState(initialValues);
  const classes = useStyle();
  return (
    <form className = {classes.root}> 
      <Grid container>
        <Grid item xs={6}>
            <TextField
            variant="outlined"
            label="Jak się nazywa Twoja firma?"
            value={values.title}
            />
            <div>
             <TextField
            variant="outlined"
            label="Cena za osobę"
            value={values.onePerson}
            />
             <TextField
            variant="outlined"
            label="Cena za dwie osoby"
            value={values.twoPeople}
            />
             <TextField
            variant="outlined"
            label="Cena za osobę dla grup"
            value={values.Group}
            />
            </div>
            <VehiclesModal />
            <div>
             <TextField
            variant="outlined"
            label="W jakim mieście?"
            value={values.city}
            />
             <TextField
            variant="outlined"
            label="Na jakiej wodzie?"
            value={values.rier}
            />
             <TextField
            variant="outlined"
            label="Podaj kod pocztowy."
            value={values.postalCode}
            />
             <TextField
            variant="outlined"
            label="Na jakiej ulicy?"
            value={values.street}
            />
              <TextField
            variant="outlined"
            label="Jaki jest numer lokalu?"
            value={values.streetNumber}
            />
            </div>
            <TextField
            variant="outlined"
            label="Poziom trudności"
            value={values.level}
            />
             <TextField
            variant="outlined"
            label="Opisz swoje miejsce"
            value={values.description}
            />

        </Grid>
        <Grid item xs={6}></Grid>
      </Grid>
    </form>
  );
}
