import React, {Component} from 'react';
import {Bar, Line, Pie} from 'react-chartjs-2';
const DATABASE_URL = "https://dancing-app-77d2a.firebaseio.com";

export default class BarChart extends Component {
  state = {
    offers: [],
      chartData : {
       labels : ["łatwy", "średni", "trudny"],
       datasets: [
         {
           label: "Population",
           data: [
            0,0,0
           ],
           backgroundColor: 
           ['#89C7CC',
           '#04738D',
           '#89C7CC',
           '#04738D',
           '#F1B71C',
           ]



  }]
}}
labels=["łatwy", "średni", "trudny"]


  componentDidMount() {
    fetch(`${DATABASE_URL}/offers.json`)
      .then((response) => response.json())
      .then((data) => {
        const formattedData = Object.keys(data).map((key) => ({
          id: key,
          ...data[key],
        }));
        this.setState({
          offers: formattedData,
        });
        this.filteringData()

      });

  }
  filteringData = () => {
    let difficultyLevels = this.labels.map((difficulty) => {
     return this.state.offers.filter((element) => {
       return element.level === difficulty;
     }).length;
   });
   this.setState({
     data: difficultyLevels,
     chartData : {
      labels : ["łatwy", "średni", "trudny"],
      datasets: [
        {
          label: "Population",
          data: [
           this.data
          ],
          backgroundColor: 
          ['#89C7CC',
          '#04738D',
          '#89C7CC',
          '#04738D',
          '#F1B71C',
          ]
        }
      ]
    }

   });
 }
  render() {

    if(this.state.offers !== undefined){
      console.log(this.state.data);

    }
    return (
      <div
        className="chart"
        style={{
          width: "600px",
          height: "400px",
          marginLeft: "100px",
        }}
      >
        <Bar
         data = {this.state.chartData}
          options={{
            title: {
              display: true,
              text: "Ilość obiektów w naszej bazie",
              fontSize: 25,
            },
            legend: {
              display: false,
              position: "right",
            },
          }}
        />
      </div>
    );
  }
}
