import { makeStyles } from '@material-ui/core/styles';

const drawerWidth = 198;

export const mainStyles = makeStyles((theme) => ({
  root: {
    display: 'flex'
  },

  content: {
    flexGrow: 1,
    minHeight: "96vh",
    width: "80vw",
    background: 'radial-gradient(circle, rgba(255,255,255,1) 0%, rgba(210,250,250,1) 100%)'

  },

}));
