import React, { useState, useEffect } from "react";
import { Grid, TextField, makeStyles } from "@material-ui/core";
import VehiclesModal, { vehiclesModal } from "./VehiclesModal";
import LevelModal from "./LevelModal";
import { useStyles } from "./OfferStyles";
import Button from "./Button";



export default class AddNewOfferForm extends React.Component {
  state= {
  classes: useStyles,
  initialValues : {
    id: 0,
    title: "",
    onePerson: "",
    twoPeople: "",
    Group: "",
    vehicles: "",
    city: "",
    river: "",
    postalCode: "",
    street: "",
    streetNumber: "",
    description: "",
    level: "",
    imageURL: "",
  }
}

render(){
  const [ children, openPopup, setOpenPopup ] = this.props;
  return (
    <form className={this.classes}>
          <div class="diwek">
            <TextField
              variant="outlined"
              label="Jak się nazywa Twoja firma?"
              value={this.state.title}
            />
          </div>
          <div style={{display: "flex", alignSelf:"center", lineHeight:1.10, width: "100%", marginTop: '10px'}}>
            <LevelModal />
            <div style={{display: "flex", width: "81%"}}>
            <TextField
              variant="outlined"
              label="Cena za osobę"
              value={this.state.onePerson}
              style={{marginLeft : "10px"}}
            />
            <TextField
              variant="outlined"
              label="Cena za dwie osoby"
              value={this.state.twoPeople}
              style={{marginLeft : "10px"}}
            />
            <TextField
              variant="outlined"
              label="Cena za osobę dla grup"
              value={this.state.Group}
              style={{marginLeft : "10px"}}
            />
            <VehiclesModal />
          </div></div>

          <div style={{
            display: "flex", 
            justifyContent: "center",
            marginTop: "10px"}}>
            <div style={{display: "flex"}}>
            <TextField
              variant="outlined"
              label="W jakim mieście?"
              value={this.state.city}
            />
            <TextField
              variant="outlined"
              label="Na jakiej wodzie?"
              value={this.state.rier}
              style={{marginLeft : "10px"}}
            />
            <TextField
              variant="outlined"
              label="Podaj kod pocztowy."
              value={this.state.postalCode}
              style={{marginLeft : "10px"}}
            />
            <TextField
              variant="outlined"
              label="Na jakiej ulicy?"
              value={this.state.street}
              style={{marginLeft : "10px"}}
            />
            <TextField
              variant="outlined"
              label="Jaki jest numer lokalu?"
              value={this.state.streetNumber}
              style={{marginLeft : "10px"}}
            />
            </div>
          </div>
          <div style={{display: "flex", width:"100%"}}>
          <TextField
            variant="outlined"
            label="Opisz swoje miejsce"
            value={this.state.description}
            style={{marginTop : "10px", width: "100%"}}
          />
          </div>
          <div style={{
          display: "flex",
          justifyContent: "center"
        }}>
        <Button
            component="div"
            color="primary"
            type="submit"
            text="Dodaj"
            className={this.classes.ButtonAdd}
            style={{ 
              width: "100px", 
              margin: "3px" }}
            onClick={() => {
              setOpenPopup(false);
            }}

          />
          <Button
            component="div"
            type="reset"
            color="primary"
            text="Resetuj"
            className={this.classes.ButtonReset}
            style={{ 
              width: "100px", 
              margin: "3px" }}
            onClick={() => {
              setOpenPopup(false);
            }}

          />
          </div>
    </form>
  );
}
}
